(()=>{console.log("This site was generated by the Hugo Framework and developped by the GdR-GPL Team at IUT de Blagnac!");document.addEventListener("DOMContentLoaded",function(){let n=document.getElementById("hero-slideshow");if(!n)return;let e=n.querySelectorAll(".slide");if(e.length<=1)return;let t=0,o=10420;function d(){e[t].classList.remove("active"),t=(t+1)%e.length,e[t].classList.add("active")}setInterval(d,o)});document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".hero-nav .dropdown-toggle-arrow").forEach(n=>{n.addEventListener("click",function(e){e.preventDefault(),this.closest("li.dropdown").classList.toggle("open")})})});})();
